import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import  MY_SERVICE from '../services/index'


export default class Layout extends Component {
    logOut = () => {
        MY_SERVICE.logOut()
          .then((response) => {
            console.log(response);
            this.setState({ loggedUser: null });
            delete localStorage.user
            return this.props.history.push('/login')
          })
          .catch((err) => console.log(err));
      };

      state ={
        user: JSON.parse(localStorage.getItem('user'))
      };

      render(){
        const {user} = this.state
        return (
          <div>
            <nav className="navbar" role="navigation" aria-label="main navigation" aria-label="dropdown navigation">
              <div className="navbar-brand">
                {/* <Link className="navbar-item" to="/"> <span className='logo'>Green Finder</span> </Link> */}
                <Link className="navbar-item" to="/"> <img src="/images/logo(green-finder).jpeg" width='150px' height="100px" alt="green-finder logo"/> </Link>
                <a role="button" className="navbar-burger burger" aria-label="menu" aria-expanded="false" data-target="navbarBasicExample">
                  <span aria-hidden="true"></span>
                  <span aria-hidden="true"></span>
                  <span aria-hidden="true"></span>
                </a>
              </div>
              <div id="navbarBasicExample" className="navbar-menu">

                <div className="navbar-end">
                  <div className="navbar-item">
                    <div className="buttons">
                      {user ? 
                        <div>
                          <div className="navbar-start">
                            <Link className="navbar-item" to="/home">Home</Link>
                            {user.role==='USUARIO' ? 
                            <Link className="navbar-item" to="/profile">Perfil</Link>
                            :
                            ''
                            }
                            <button onClick={this.logOut} className="button is-primary">Cerrar Sesión</button>
                          </div>
                        </div>  
                        :
                        <div>
                          <Link className="button is-primary" to="/signup">
                            <strong>Crear cuenta</strong>
                          </Link>
                          <Link className="button is-light" to="/login">Iniciar Sesión</Link>
                        </div>
                      }
                    </div>
                  </div>
                </div> 
              </div>
            </nav>
            {this.props.children}
            <footer className="footer">
              <div className="content has-text-centered">
                <p className="has-text-white">
                  <strong className="has-text-white">Green Finder</strong> by <a href="https://github.com/TanMar23">Tania Marin</a>. The source code is licensed
                </p>
              </div>
            </footer>
          </div>
        )
      }
    }
