import React, { Component } from 'react';
import MY_SERVICE from '../../services/index';

class Signup extends Component {
    state = {
      user: {
        campus: '',
        course: ''
      }
    };

    handleInput = (e) => {
      const { user } = this.state;
      const key = e.target.name;
      user[key] = e.target.value;
      this.setState({ user });
    };

    onSubmit = (e) => {
      e.preventDefault();
      MY_SERVICE.signup(this.state.user)
        .then((response) => {
          let userMsg = response.data.msg
          console.log(userMsg);
        })
        .catch((error) => {
          console.log(error);
        });
    };

    render() {
      const {course, campus, userMsg} = this.state.user
      return (
        <div className="columns is-centered">
          <div className="column box ironBox is-10">
            <p className="mesaage is-success">{userMsg}</p>
            <form className="columns iron-height100" onSubmit={this.onSubmit}>
              <div className="column is-7 iron-cover ironHome">
                <h1 className="title is-2">Sign up</h1>
                <div className="field">
                  <label className="label">Email:</label>
                  <input className="input ironInput" onChange={this.handleInput} type="text" name="name" />
                </div>
                <div className="field">
                  <label className="label">Nombre:</label>
                  <input className="input ironInput" onChange={this.handleInput} type="text" name="name" />
                </div>
                <div className="field">
                  <label className="label">Apellido:</label>
                  <input className="input ironInput" onChange={this.handleInput} type="text" name="apellido" />
                </div>
                <div className="field">
                  <label className="label">Contrase√±a:</label>
                  <input className="input ironInput" onChange={this.handleInput} type="password" name="password" />
                </div>
                <div>

                </div>
              </div>
            </form>
          </div>
        </div>
      );
    }
  }

//   

  export default Signup;
