import React, { Component } from 'react'
// import { Link } from 'react-router-dom'
import axios from 'axios'


export default class Center extends Component {
    state = {
        center: {}
    }

    getCenter = async () => {
        const response = await axios.get(`http://localhost:3000/api/centers/${this.props.match.params.id}`)        
        this.setState({
            center: response.data
        })
        console.log(response.data);
    }

    componentDidMount() {
        this.getCenter()
    }


    render() {
        const { centers } = this.state
        console.log(centers);
        return (
            <div>
                <p>Centro</p>
            </div>
        )
    }
}
