import React, { Component } from 'react'
import MY_SERVICE from '../../services/index'

export default class CreateNew extends Component {
    state={
        center: {
            empresa: "",
            email: "",
            website: "",
            address: "",
            horario: "",
            contactNumber: "",
            tipoResiduo: ""
        }
    }

    handleInput = (e) => {
        const { center } = this.state;
        const key = e.target.name;
        center[key] = e.target.value;
        this.setState({ center });
      };


      addCenter = (e) => {
        e.preventDefault();
        MY_SERVICE.addCenter(this.state.center)
          .then((response) => {
            console.log(response);
          })
          .catch((error) => {
            console.log(error);
          });
      };


    render() {
        let  center =this.state
        console.log(this.state.center);

        return (
            <div className="new-center-wrapper">
            <div className="columns is-centered">
              <div >
                <form className="columns is-5" onSubmit={center.addCenter}>
                  <div className="column">
                    <h1 className="title is-1">Agregar centro</h1>
                      <label className="label is-size-5">Nombre:</label>
                      <input className="input" onChange={center.handleInput} type="text" name="empresa"/>


                      <label className="label is-size-5">Email:</label>
                      <input className="input" onChange={center.handleInput} type="email" name="email"/>

                      <label className="label is-size-5">Sitio Web:</label>
                      <input className="input" onChange={center.handleInput} type="text" name="website"/>

                      <label className="label is-size-5">Direccion:</label>
                      <input className="input" onChange={center.handleInput} type="text" name="address"/>

                      <label className="label is-size-5">Horario:</label>
                      <input className="input" onChange={center.handleInput} type="text" name="horario"/>

                      <label className="label is-size-5">Direccion:</label>
                      <input className="input" onChange={center.handleInput} type="number" name="contactNumber"/>

                      {/* Categoria debe ser un select */}

                      <label className="label is-size-5">Categoria:</label>
                      <input className="input" onChange={center.handleInput} type="text" name="tipoResiduo"/>

                    <div>
                    <input className="button is-primary is-fullwidth" type="submit" value="Agregar centro"/>
                  </div>
                </div>
              </form>
            </div>
          </div>
          </div>

        )
    }
}
